  <h1 class="text2 head"><span class="text-primary">SIAT</span> - <span class="text-warning">NIC</span></h1>


  <div class="container" *ngIf="search">
    <p class="text2">Encuentra tu aula de votaciÃ³n ðŸ˜‰ðŸ—³</p>
    <div class="row  justify-content-center">
      <div class="col-md-6 col-lg-4 ">
        <div class="d-flex flex-column align-items-center">
        <input type="text" class="form-control" [(ngModel)]="lastName" placeholder="Ingresa tu apellido">
        <button type="button" (click)="seeClassRoom()"  class="btn btn-primary text mt-2"> BUSCAR <i class="bi bi-search"></i></button>
        </div>
      </div>
    </div>

    <div class="col-12 d-flex justify-content-center align-items-center">
      <img class="mt-3 img-fluid" src="assets/img/primero.jpg" alt="logo" width="700">
    </div>
  </div>

  <div class="container" *ngIf="!search">
    <h2 class="text2">AULA DE VOTACIÃ“N</h2>
    <div class="row  justify-content-center">
      <div class="col-md-6 col-lg-4 ">
        <div class="d-flex flex-column align-items-center">
          <p>{{results?.classRoom}}</p>
          <p>Mesa : {{results?.tableNum}}</p>
          <p>Rango: {{results?.initA}} {{results?.initB}} - {{results?.endA}} {{results?.endB}} </p>
        <button type="button" (click)="clean()"  class="btn btn-primary text mt-2"> BUSCAR NUEVAMENTE <i class="bi bi-search"></i></button>
        </div>
      </div>
    </div>

    <div class="col-12 d-flex justify-content-center align-items-center">
      <img class="mt-3 img-fluid" src="assets/img/segundo.jpg" alt="logo" width="700">
    </div>
  </div>
</div>
