  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  title = 'votingPage';
  lastName:string ="";
  search:boolean=true;


  seeClassRoom(){
    this.search = false;
  }
}

